class Ui_SSRRecognition(object):

    ## Starting the Exepriment Function
    def StartSpatialRecogExperiment(self):
        self.StageControllerPort, self.DataCollectorPort = My_utils.COMFinder(self)
        try:
            self.NumberOfSamples = int(self.SampleSizeInput.text())

            try:
                self.Period = int(self.PeriodInput.text())
                
                try:
                    self.NumberOfChannels = int(self.ChannelNumberInput.text())

                    try:
                        self.Increment = int(self.IncrementInput.text())
                        
                        try:
                            self.RotAngle = int(self.RotAngleInput.text())
                            self.Direction = self.DirectionSectionComboBox.currentText()
                            if self.Direction == "Anti-ClockWise":
                                self.Direction = 0
                            else:
                                self.Direction = 1
                            
                            self.DialogBoxOutput.setText("Success! \nStarting the Experiment Now... ")
 
                        except:
                            print("Rotation angle must be an integer!")
                            self.DialogBoxOutput.setText("Rotation angle must be an integer!") 
                    except:
                        print("Increments must be an integer!")
                        self.DialogBoxOutput.setText("Increments must be an integer!")               
                except:
                    print("Number of Channels must be an integer!")
                    self.DialogBoxOutput.setText("Number of Channels must be an integer!")
            except:
                print("Period must be an integer!")
                self.DialogBoxOutput.setText("Period must be an integer!")
        except:
            print("Sample number must be an integer!")
            self.DialogBoxOutput.setText("Sample number must be an integer!")

